mapper.select:
		SELECT NVL(MIN(groupOrd),0) FROM train WHERE  originNo = #{originNo}
   		AND groupOrd > #{groupOrd} 
  		AND groupLayer <= #{groupLayer}

		select * from train
 		where originNo = #{originNo}
   		and groupOrd > #{groupOrd}

		select * from train
 		where originNo = #{originNo}
   		and groupOrd > #{groupOrd}
   		and groupOrd < #{groupOrdTail}
